import React from 'react';
import './Registration.css';

const Registration = () => {


  const [fname, fsetName] = useState("");
  const [lname, lsetName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [rpassword, rsetPassword] = useState("");


  const [flag, setFlag] = useState(false);
  const [login, setLogin] = useState(true);



  function handleFormSubmit(e) {
    e.preventDefault();

    if (!fname || !lname || !email || !password || !rpassword) {
      setFlag(true);
    } else {
      setFlag(false);
      localStorage.setItem("Email", JSON.stringify(email));
      localStorage.setItem(
        "Password",
        JSON.stringify(password)
      );
      console.log("Saved in Local Storage");

      setLogin(!login);
    }
  }

  function handleClick() {
    setLogin(!login);
  }



    return (
        <div className='container' id='Registration'>
            <div className='row'>
                <div className='col'>
                    <div className='form-containt'>
                        <div className='form-heading'>
                            <h2 className='title-text'>Create account</h2>
                            <p className='sub-text'>Already have an account? <a href='#'>Sign in</a></p>
                        </div>
                        <div className='form-detail'>
                            <form>
                                <input type='email' placeholder='Email' /><br />
                                <div className='name-detail'>
                                    <input type='text' placeholder='First name' className='firstname' />
                                    <input type='text' placeholder='Last name' className='lastname' /><br />
                                </div>
                                <input type='password' placeholder='Password' /><br />
                                <input type='password' placeholder='Repeat Password' /><br />
                                <button className='btn btn-primary'>Sign up <i class="fa-solid fa-arrow-right icon"></i></button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Registration




